export default function user() {
  if( JSON.parse(localStorage.getItem('user')).valid())
  {
    const user = JSON.parse(localStorage.getItem('user'));
  
    if (user) {
      return user;
     
    } else {
      return null;
    }
  }
    else return null;
  }
