export default function user() {
  if(typeof localStorage.getItem('user') !== 'undefined')
  {
    const user = JSON.parse(localStorage.getItem('user'));
  
    if (user) {
      return user;
     
    } else {
      return null;
    }
  }
    else return null;
  }
